YUI.add("gallery-math",function(A){A.mix(Math,{add:function(){return A.Array.reduce(A.Array(arguments),0,function(C,B){if(A.Lang.isArray(B)){B=Math.add.apply(this,B);}return C+B;});},addReciprocals:function(){return A.Array.reduce(A.Array(arguments),0,function(C,B){if(A.Lang.isArray(B)){return C+Math.addReciprocals.apply(this,B);}else{return C+1/B;}});},parallel:function(){return 1/Math.addReciprocals.apply(this,arguments);},multiply:function(){return A.Array.reduce(A.Array(arguments),1,function(C,B){if(A.Lang.isArray(B)){B=Math.multiply.apply(this,B);}return C*B;});},degreesToRadians:function(B){return B*Math.PI/180;},radiansToDegrees:function(B){return B*180/Math.PI;},acosh:function(B){return Math.log(B+Math.sqrt(B*B-1));},asinh:function(B){return Math.log(B+Math.sqrt(B*B+1));},atanh:function(B){return Math.log((1+B)/(1-B))/2;},cosh:function(B){var C=Math.exp(B);return(C+1/C)/2;},sinh:function(B){var C=Math.exp(B);return(C-1/C)/2;},tanh:function(B){var C=Math.exp(2*B);return(C-1)/(C+1);}});},"@VERSION@",{requires:["array-extras"]});