YUI.add("gallery-test-extras",function(B){var A=1/Math.log(10);B.mix(B.Assert,{areWithinEpsilon:function(C,F,E){if(B.Lang.isUndefined(E)){E=1e-9;}if(C!==0){var D=Math.pow(10,Math.floor(Math.log(Math.abs(C))*A));C/=D;F/=D;}B.Assert.isTrue(Math.abs(C-F)<=E,"Values should be within "+E+"\nExpected: "+C+" (number)\nActual:"+F+" (number)\n-----");},complexWithinEpsilon:function(C,E,D){B.Assert.areWithinEpsilon(C.r,E.r,D);B.Assert.areWithinEpsilon(C.i,E.i,D);}});},"@VERSION@",{optional:["gallery-complexnumber"],requires:["test"]});