YUI.add("gallery-test-extras",function(e,t){"use strict";var n=1/Math.log(10);e.mix(e.Assert,{areWithinEpsilon:function(t,r,i){e.Lang.isUndefined(i)&&(i=1e-9);if(t!==0){var s=Math.pow(10,Math.floor(Math.log(Math.abs(t))*n));t/=s,r/=s}e.Assert.isTrue(Math.abs(t-r)<=i,"Values should be within "+i+"\nExpected: "+t+" (number)\nActual:"+r+" (number)\n-----")},complexWithinEpsilon:function(t,n,r){e.Assert.areWithinEpsilon(t.r,n.r,r),e.Assert.areWithinEpsilon(t.i,n.i,r)}})},"@VERSION@",{requires:["test"],optional:["gallery-complexnumber"]});
